import syssys.path.append('..')import osimport shutildef copy(src, dst):    if os.path.isfile(src):        shutil.copyfile(src, dst)    else:        shutil.copytree(src, dst)SERVER_FILES = ['maps', 'config.txt', 'scripts', 'web']from pyspades.common import crc32version = crc32(open('../data/client.exe', 'rb').read())open('./dist/client_version', 'wb').write(str(version))open('./dist/run.bat', 'wb').write('run.exe\npause\n')for name in SERVER_FILES:    copy('../feature_server/%s' % name, './dist/%s' % name)